sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	var urgencyValue = g_form.getValue('urgency');
	var impactValue = g_form.getValue('impact');

	// Logic 
	if(urgencyValue == '1' && impactValue == '1'){
		g_form.setValue('priority','1');
	} else if(urgencyValue == '2' && impactValue == '1'){
		g_form.setValue('priority','1');
	} else if(urgencyValue == '2' && impactValue == '2'){
		g_form.setValue('priority','2');
	} else if(urgencyValue == '3' && impactValue == '2'){
		g_form.setValue('priority','2');
	} else if(urgencyValue == '3' && impactValue == '3'){
		g_form.setValue('priority','3');
	} else {
		g_form.setValue('priority','10');
	}


}
