function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var deviceName = g_form.getReference('medical_device', callback);
	//alert('hii');

    function callback(deviceName) {
		//alert('Device Name : '+deviceName.device_name);
        alert('Device Type : ' + deviceName.device_type);

        g_form.setValue('category', deviceName.device_type);

        var catValue = g_form.getValue('category');
        //alert("Category Value : "+catValue);

        if (catValue == 'monitoring_equipment') {
            g_form.addInfoMessage('test 1');
            g_form.clearOptions('subcatrgory');
            g_form.addOption('subcatrgory', 'none', '-----None-----');
            g_form.addOption('subcatrgory', 'device_malfunction', 'Device Malfunction');
            g_form.addOption('subcatrgory', 'connection_issues', 'Connection Issues');
            g_form.addOption('subcatrgory', 'alarm_notifications', 'Alarm Notifications');
            g_form.addOption('subcatrgory', 'data_accuracy_concerns', 'Data Accuracy Concerns');
        } else if (catValue == 'infusion_device') {
            g_form.addInfoMessage('test 2');
            g_form.clearOptions('subcatrgory');
            g_form.addOption('subcatrgory', 'none', '-----None-----');
            g_form.addOption('subcatrgory', 'flow_rate_irregularities', 'Flow Rate Irregularities');
            g_form.addOption('subcatrgory', 'occlusion_alarms', 'Occlusion Alarms');
            g_form.addOption('subcatrgory', 'battery_or_power_issues', 'Battery or Power Issues');
            g_form.addOption('subcatrgory', 'medication_administration_errors', 'Medication Administration Errors');
        } else if (catValue == 'imaging_equipment') {
            g_form.addInfoMessage('test 3');
            g_form.clearOptions('subcatrgory');
            g_form.addOption('subcatrgory', 'none', '-----None-----');
            g_form.addOption('subcatrgory', 'image_quality_issues', 'Image Quality Issues');
            g_form.addOption('subcatrgory', 'radiation_exposure_concerns', 'Radiation Exposure Concerns');
            g_form.addOption('subcatrgory', 'mechanical_failures', 'Mechanical Failures');
            g_form.addOption('subcatrgory', 'software_errors', 'Software Errors');
        } else if (catValue == 'cardiac_equipment') {
            g_form.addInfoMessage('test 4');
            g_form.clearOptions('subcatrgory');
            g_form.addOption('subcatrgory', 'none', '-----None-----');
            g_form.addOption('subcatrgory', 'failure_to_deliver_shock', 'Failure to Deliver Shock');
            g_form.addOption('subcatrgory', 'electrode_connection_problems', 'Electrode Connection Problems');
            g_form.addOption('subcatrgory', 'battery_or_power_failures', 'Battery or Power Failures');
            g_form.addOption('subcatrgory', 'inaccurate_ecg _readings', 'Inaccurate ECG Readings');
        } else if (catValue == 'respiratory_equipment') {
            g_form.addInfoMessage('test 5');
