            g_form.addOption('subcatrgory', 'none', '-----None-----');
            g_form.addOption('subcatrgory', 'image_clarity_concerns', 'Image Clarity Concerns');
            g_form.addOption('subcatrgory', 'probe_malfunctions', 'Probe Malfunctions');
            g_form.addOption('subcatrgory', 'software_bugs', 'Software Bugs');
            g_form.addOption('subcatrgory', 'scanning_or_malibration_issues', 'Scanning or Calibration Issues');
        } else if (catValue == 'surgical_equipment') {
            g_form.addInfoMessage('test 8');
            g_form.clearOptions('subcatrgory');
            g_form.addOption('subcatrgory', 'none', '-----None-----');
            g_form.addOption('subcatrgory', 'power_fluctuations', 'Power Fluctuations');
